import { Component, Inject } from '@angular/core';
import { HttpClient, HttpXhrBackend } from '@angular/common/http';

@Component({
  selector: 'app-films-component',
  templateUrl: './films.component.html'
})

export class FilmsComponent {

  public results: Results[] = [];

  constructor(http: HttpClient) {
    http.get<Film>("https://swapi.dev/api/films").subscribe(result => {
      this.results = result.results;
    }, error => console.error(error));
  }

  getFilmCharacters(id: number) {
    var result = this.results.find(x => x.episode_id == id);

    var index = this.results.findIndex(x => x.episode_id == id);

    if (index >= 0 && this.results[index].charactersInfo == null) {

      this.results[index].charactersInfo = [];

      const httpClient = new HttpClient(new HttpXhrBackend({
        build: () => new XMLHttpRequest()
      }));

      result?.characters.forEach(x => {
        httpClient.get<Character>(x).subscribe(result => {
          this.results[index].charactersInfo.push(result);
        }, error => console.error(error));
      }) 
    }   
  }

  getFilmPlanets(id: number) {
    var result = this.results.find(x => x.episode_id == id);

    var index = this.results.findIndex(x => x.episode_id == id);

    if (index >= 0 && this.results[index].planetsInfo == null) {

      this.results[index].planetsInfo = [];

      const httpClient = new HttpClient(new HttpXhrBackend({
        build: () => new XMLHttpRequest()
      }));

      result?.planets.forEach(x => {
        httpClient.get<Planet>(x).subscribe(result => {
          this.results[index].planetsInfo.push(result);
        }, error => console.error(error));
      })
    }
  }
}

interface Film {
  count: string;
  results: Results[];
}

interface Results {
  title: string;
  episode_id: number;
  opening_crawl: string;
  director: string;
  producer: string;
  release_date: string;
  characters: string[];
  charactersInfo: Character[];
  planets: string[];
  planetsInfo: Planet[];
}

interface Character {
  name: string;
  height: string;
  mass: string;
  hair_color: string;
  skin_color: string;
  eye_color: string;
  birth_year: string;
  gender: string
}

interface Planet {
  name: string;
  rotation_period: string;
  orbital_period: string;
  diameter: string;
  climate: string;
  gravity: string;
  terrain: string;
  surface_water: string
  population: string;
}
<h1>Films</h1>

<div *ngIf="results.length == 0">
  <p>awdawd</p>

</div>

<div class="accordion" id="accordionExample">
  <div *ngFor="let film of results">
    <div class="accordion-item">
      <h2 class="accordion-header">
        <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" attr.data-bs-target="#collapse{{ film.episode_id }}" aria-expanded="false" attr.aria-controls="collapse{{ film.episode_id }}">
          {{ film.title }}
        </button>
      </h2>
      <div id="collapse{{ film.episode_id }}" class="accordion-collapse collapse" data-bs-parent="#accordionExample">
        <div class="accordion-body">
          <p>{{ film.opening_crawl }}</p>
          <p><strong>Director:</strong> {{ film.director }}</p>
          <p><strong>Producer:</strong> {{ film.producer }}</p>
          <p><strong>Release:</strong> {{ film.release_date }}</p>

          
          <div class="accordion accordion-flush" id="accordionFlushCha">
            <div class="accordion-item">
              <h2 class="accordion-header">
                <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" attr.data-bs-target="#flush-collapse{{ film.episode_id }}cha" aria-expanded="false" attr.aria-controls="flush-collapse{{ film.episode_id }}cha" (click)="getFilmCharacters(film.episode_id)">
                  Characters
                </button>
              </h2>
              <div id="flush-collapse{{ film.episode_id }}cha" class="accordion-collapse collapse" data-bs-parent="#accordionFlushCha">
                <div class="accordion-body">
                  <table class="table">
                    <thead>
                      <tr>
                        <th scope="col">#</th>
                        <th scope="col">Name</th>
                        <th scope="col">Height</th>
                        <th scope="col">Mass</th>
                        <th scope="col">Hair Color</th>
                        <th scope="col">Skin Color</th>
                        <th scope="col">Eye Color</th>
                        <th scope="col">Birth Year</th>
                        <th scope="col">Gender</th>
                      </tr>
                    </thead>
                    <tbody *ngFor="let character of film.charactersInfo; index as i">
                      <tr>
                        <th scope="row">{{ i + 1}}</th>
                        <td>{{ character.name }}</td>
                        <td>{{ character.height }}</td>
                        <td>{{ character.mass }}</td>
                        <td>{{ character.hair_color }}</td>
                        <td>{{ character.skin_color }}</td>
                        <td>{{ character.eye_color }}</td>
                        <td>{{ character.birth_year }}</td>
                        <td>{{ character.gender }}</td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>

          <div class="accordion accordion-flush" id="accordionFlushPla">
            <div class="accordion-item">
              <h2 class="accordion-header">
                <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" attr.data-bs-target="#flush-collapse{{ film.episode_id }}pla" aria-expanded="false" attr.aria-controls="flush-collapse{{ film.episode_id }}pla" (click)="getFilmPlanets(film.episode_id)">
                  Planets
                </button>
              </h2>
              <div id="flush-collapse{{ film.episode_id }}pla" class="accordion-collapse collapse" data-bs-parent="#accordionFlushPla">
                <div class="accordion-body">
                  <table class="table">
                    <thead>
                      <tr>
                        <th scope="col">#</th>
                        <th scope="col">Name</th>
                        <th scope="col">Rotation Period</th>
                        <th scope="col">Orbital Period</th>
                        <th scope="col">Diameter</th>
                        <th scope="col">Climate</th>
                        <th scope="col">Gravity</th>
                        <th scope="col">Terrain</th>
                        <th scope="col">Surface Water</th>
                        <th scope="col">Population</th>
                      </tr>
                    </thead>
                    <tbody *ngFor="let planet of film.planetsInfo; index as i">
                      <tr>
                        <th scope="row">{{ i + 1}}</th>
                        <td>{{ planet.name }}</td>
                        <td>{{ planet.rotation_period }}</td>
                        <td>{{ planet.orbital_period }}</td>
                        <td>{{ planet.diameter }}</td>
                        <td>{{ planet.climate }}</td>
                        <td>{{ planet.gravity }}</td>
                        <td>{{ planet.terrain }}</td>
                        <td>{{ planet.surface_water }}</td>
                        <td>{{ planet.population }}</td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>

        </div>
      </div>
    </div>
  </div>
</div>
